import React from 'react';
import ReactDOM from 'react-dom';

const Part = (props) => {
  return(
    <p>{props.part} {props.excercise}</p>
  )
}

const Head = (props) => {
  return (
    <p>{props.course}</p>
  )
}

const Content = (props) => {
  return(
    <>
      <Part part={props.part1} excercise={props.excercise1}/>
      <Part part={props.part2} excercise={props.excercise2}/>
      <Part part={props.part3} excercise={props.excercise3}/>
    </>
  )
}

const Total = (props) => {
  return (
    <p>Number of excercises {props[0] + props.excercise2 + props.excercise3}</p>
  )
} 

const App = () => {
  const course = "Half Stack application development"
  const parts = [
    {
      name: 'Fundamentals of React',
      excercise: 10,
    },
    {
      name: 'Using props to pass data',
      excercise: 7,
    },
    {
      name: 'State of a component',
      excercise: 14,
    }
  ]

  return(
  <div>
    <Head course={course}/>
    <Content parts={parts}/>
    <Total parts={parts} />
  </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
